tinymce.PluginManager.add('uploadfile', function (editor, url) {
    editor.addButton('uploadfile', {
        icon: 'copy',
        tooltip: 'Upload File',
        onclick: function () {
            document.getElementById('uploadFiles').click();
        }
    });

    

    editor.addMenuItem('uploadfile', {
        text: 'Upload File',
        context: 'upload',
        onclick: function () {
            document.getElementById('uploadFiles').click();

        }
    });
});